"use strict";
var jsdom = require('jsdom');
var ExecutionEnvironment = require('../../fbjs/lib/ExecutionEnvironment');
ExecutionEnvironment.canUseDOM = true;
exports.kickstart = function () {
  var g = global;
  var document = jsdom.jsdom('<!doctype html><html><body></body></html>');
  g.document = document;
  g.window = document.defaultView;
  g.navigator = {
    userAgent: 'testing',
    platform: []
  };
  g.HTMLElement = g.window.HTMLElement;
  g.Element = g.window.Element;
};
exports.cleanup = function () {
  var g = global;
  delete g.document;
  delete g.window;
  delete g.navigator;
};
exports.kickstart();
beforeEach(exports.kickstart);
afterEach(exports.cleanup);
